        const arrData = decode.split(";");
        this.servers = 'https://' + arrData[0] + '/api/web';

        // this.$store.commit('saveHost', 'http://' + arrData[0] + '/api/web');
        // this.$store.commit('saveBranch', arrData[1]);
        // this.$store.commit('saveTable', arrData[2]);

        axios.get(this.servers + '/bill/' + this.$route.query.scr)
        .then(() => {
            this.$router.push({name: 'Receipt', query: {scr: this.$route.query.scr}});
        }).catch(() => { 
            axios.get(this.servers + '/cart/' + this.$route.query.scr)
            .then((res) => {
                if(res.data.is_submitted) this.$router.push({name: 'Submitted', query: {scr: this.$route.query.scr}});
            }).catch(() => { window.location.href = "/" });
        })
    },  
}
</script>

