<script>
import axios from 'axios';
import { StreamBarcodeReader } from "vue-barcode-reader";
import { Splide, SplideSlide } from '@splidejs/vue-splide';
import '@splidejs/vue-splide/css';
import '@splidejs/vue-splide/css/core';

export default {
  name: 'LandingView',
  components: {
    Splide,
    SplideSlide,
    StreamBarcodeReader
  },
  props: ['main'],
  data(){
    return {  
      isActiveCamera: false,
      cameraText: 'Open Camera',
      qrText: null,
      firstFeatures: true,
      secondFatures: false,
      thirdFeatures: false,
      fourthFeatures: false,
      isActive: 'first',
      homePage: true,
      mainPage: false,
      restaurant: [],
      outlet: [],
      servers: '',
      screenposition: 0,
    }
  },
  mounted(){
      this.getRestaurant()
  },
  created(){
      window.addEventListener('scroll', this.handleScroll);
      this.handleScroll();
  },
  methods: {
    handleScroll() {
        this.screenposition = window.scrollY;
    },
    async getRestaurant(){
        try { 
            let { data } = await axios.get('https://api.tamani-pos.online/api/v1/restaurant-outlet')
            this.restaurant = data.data
            this.restaurant.forEach(element => {
                element.outlet.forEach(element => {
                    this.outlet.push(element)
                });
            });
        } catch(e){
            console.log(e)
        }
    },
    activateScanner(){
      if(this.isActiveCamera === false){
        this.isActiveCamera = true;
        this.cameraText = 'Close Camera';
      } else {
        this.isActiveCamera = false;
        this.cameraText = 'Open Camera';
      }
    },
    async onDecode(text){
        console.log(`Decode text from QR code is ${text}`)
        let arrText = text.split("=");
        
        try {
          const decode = atob(arrText[1]);
          const arrData = decode.split(";");
          this.servers = 'https://' + arrData[0] + '/api/web';  
          await axios.get(this.servers + '/home/' + arrText[1]);
          this.$router.push({name: 'Branch', query:{scr: arrText[1]}});            
        } catch(e){
          window.navigator.vibrate(300);
          alert('Invalid QR Code'); 
        }
    },
    first(){
      this.isActive = 'first';
      this.firstFeatures = true;
      this.secondFatures = false;
      this.thirdFeatures = false;
      this.fourthFeatures = false;
    },
    second(){
      this.isActive = 'second';
      this.firstFeatures = false;
      this.secondFatures = true;
      this.thirdFeatures = false;
      this.fourthFeatures = false;
    },
    third(){
      this.isActive = 'third';
      this.firstFeatures = false;
      this.secondFatures = false;
      this.thirdFeatures = true;
      this.fourthFeatures = false;
    },
    fourth(){
      this.isActive = 'fourth';
      this.firstFeatures = false;
      this.secondFatures = false;
      this.thirdFeatures = false;
      this.fourthFeatures = true;
    },
    sMainPage(params){
      this.homePage = false;
      this.mainPage = params;
    },
    getRestaurantImg(filename){
      return 'https://api.tamani-pos.online/assets/images/restaurant/' + filename;
    }
  }
}
</script>

